// Google Reader Content Retrieve
// version 2.0
// 2011-09-06
// ==UserScript==
// @name          Google Reader Content Retrieve
// @namespace     http://google.com/reader/greasemonkey/content
// @description   Retrieve content in Google Reader.
// @include    http*://reader.google.com/*
// @include    http*://www.google.com/reader/*
// @include    http*://google.com/reader/*
// ==/UserScript==


var doc = document,
    currentEntry, lastEntry, 
    rscript = /<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi,
    hiddenDiv = doc.createElement('div'),
    emptyDiv = doc.createElement('div'),
    loader = doc.createElement('img'),
    marker = doc.createElement('img'),
    retrieveLink = doc.createElement('a'),
    siteParser = [
        {key:'hk.news.yahoo.com', selector:'div.yom-art-content div.bd'},
        {key:'mysinablog.com', selector:'div.post_content'},
        {key:'blog.xuite.net', selector:'div.blogbody'},
        {key:'blog.roodo.com', selector:'div.blogbody'},
        {key:'hk.myblog.yahoo.com', selector:'div.main-bd'},
        {key:'blog.yam.com', selector:'div#post_content'},
        {key:'www.littleoslo.com', selector:'div.hentry-content.clear'},
        {key:'wanszezit.wordpress.com', selector:'div.entry-content'},
        {key:'www.wretch.cc/blog', selector:'div.innertext'}
    ];

//Init basic elements
loader.src = 'data:image/gif;base64,%3D%3D';
marker.src = 'data:image/gif;base64,R0lGODlhCwALALMAAFHGKVLGKsXrucrtvkvFIvX781PGLFXHL/f89fD67cntvVLGK////1XHLgAAAAAAACH5BAAAAAAALAAAAAALAAsAAAQ2kKVxmm0qMbSW7d6CKF7DnJ8SlCfzAQeLNosRt3jdmThtEDIXDSDYmT4LRcF46SBOioslw4gAADs=';
retrieveLink.href = "#";
retrieveLink.style.display = "block";
retrieveLink.className = 'rssget-retrieve';
retrieveLink.innerHTML = "Retrieve content";
retrieveLink.appendChild(marker);

function getCurrentEntry(){
    currentEntry = doc.getElementById('current-entry');
    return currentEntry || emptyDiv;
}

function getCurrentLink(){
    var a = getCurrentEntry().querySelectorAll('a');
    return a.length?a[0].href:"";
}

function getCurrentParser(){
    var link = getCurrentLink();
    for(var i=0, l=siteParser.length; i<l; i++){
    var p = siteParser[i];
    var k = p.key.replace(/new_s/g, 'news'); //strange in chrome, plugin problem??
        if(link.indexOf(k) != -1){
            p.link = link;
            return p;
        }
    }
    return null;
}

function stripScript(str){
    return str.replace(rscript, '');
}

function getContent(str, selector){
    hiddenDiv.innerHTML = stripScript(str);
    var html =  hiddenDiv.querySelectorAll(selector)[0].innerHTML;
    hiddenDiv.innerHTML = ""; //clean
    return html;
}

function getItemDiv(entry){
    return (entry || getCurrentEntry()).querySelectorAll('div.item-body>div')[0];
}

function replaceContent(str){
    getItemDiv().innerHTML = str;
}

function appendLoader(){
    getItemDiv().appendChild(loader.cloneNode(false));
}

function retrieveCurrentContent(){
    var parser = getCurrentParser();
    if(parser){
        appendLoader();
        GM_xmlhttpRequest({
            method: "GET",
            url: parser.link,
            onload: function(response){
                var content = getContent(response.responseText, parser.selector);
                replaceContent(content);
            }
        });
    }
}

function addMarker(){
    var entry = getCurrentEntry();
    if(entry != emptyDiv && entry != lastEntry){
        var parser = getCurrentParser();
        if(parser){
            var item = getItemDiv();
            if(!item.dataset.markerAdded){
                newLink = retrieveLink.cloneNode(true);
                newLink.addEventListener("click", function(){
                    retrieveCurrentContent();
                    return false;
                }, false);
                item.appendChild(newLink);
            }
            item.dataset.markerAdded = true;
            lastEntry = entry;
        }
    }
}
    
function linkKey(event) {
    var k = String.fromCharCode(event.which);
    if (k == "g") retrieveCurrentContent();
}

// Add listener for to handle the keypress events.
doc.addEventListener("keypress", linkKey, true);

setInterval(addMarker, 100);
